import  React from 'react';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer,getFocusedRouteNameFromRoute, DrawerActions, } from '@react-navigation/native';
import WelcomeScreen from '../screens/WelcomeScreen';
import RegisterScreen from '../screens/RegisterScreen';
import LoginScreen from '../screens/LoginScreen';
import ForgotPasswordScreen from '../screens/ForgotPasswordScreen';
import { createDrawerNavigator } from '@react-navigation/drawer';
import HomeScreen from '../screens/HomeScreen';
import { View, StyleSheet,TextInput,ScrollView,Button } from 'react-native';
//import handleSignOut from '../screens/Signout'
import firebase from 'firebase/app';
import 'firebase/auth';
import 'firebase/firestore';
require('firebase/firestore');
import firebaseConfig from '../components/Firebase/firebaseConfig';
firebase.auth().onAuthStateChanged(user => {
  if (user) {
      navigate('Home');
  } else {
      navigate('Login');
  }
});
const Drawer = createDrawerNavigator();

const HomeDrawer = () => {


  const signOutUser = async () => {
    try {
        await firebase.auth().signOut();
        navigate('LoginScreen');
    } catch (e) {
        console.log(e);
    }
}


  return (
         <Drawer.Navigator>
            <Drawer.Screen name="Home" component={HomeScreen} />
            <Drawer.Screen name="sign " component={signOutUser} />
            <Drawer.Screen name="Password Forget" component={ForgotPasswordScreen} />


         </Drawer.Navigator>
       );
      };

const Stack = createStackNavigator();
 export default function AuthStack() {


  return (
    <NavigationContainer independent={true}>
    <Stack.Navigator initialRouteName={WelcomeScreen}>

          <Stack.Screen name="Home" component={HomeDrawer}
          options={({ route, navigation }) => ({
            headerTitle: getFocusedRouteNameFromRoute(route),
            headerLeft: () => (
              <Button
                onPress={() =>
                  navigation.dispatch(DrawerActions.toggleDrawer())
                }
                title="Menu"
              />
            ),

          })}/>

      <>
      <Stack.Screen
              name="Welcome"
              component={WelcomeScreen}
              options={{
                animationTypeForReplace: 'pop',
              }}/>

             <Stack.Screen name="Login" component={LoginScreen} />




             <Stack.Screen name="Register" component={RegisterScreen} />



                 <Stack.Screen name="ForgotPassword" component={ForgotPasswordScreen} />
                 </>


    </Stack.Navigator>
    </NavigationContainer>
  );
};
