import React, { useContext, useState, useEffect } from 'react';
import { View, StyleSheet, Keyboard,Button } from 'react-native';
import { NavigationContainer,getFocusedRouteNameFromRoute, DrawerActions, } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { TodoContext } from '../context/TodoContext';
import WelcomeScreen from '../../screens/WelcomeScreen';
import RegisterScreen from '../../screens/RegisterScreen';
import LoginScreen from '../../screens/LoginScreen';
import ForgotPasswordScreen from '../../screens/ForgotPasswordScreen';
import { createDrawerNavigator } from '@react-navigation/drawer';
import HomeScreen from '../../screens/HomeScreen';
import TabItem from './TabItem';

const Drawer = createDrawerNavigator();
const HomeDrawer = () => {


  return (
         <Drawer.Navigator>
            <Drawer.Screen name="Home" component={HomeScreen} />

            <Drawer.Screen name="Password Forget" component={ForgotPasswordScreen} />



         </Drawer.Navigator>
       );
      };
      const Stack = createStackNavigator();

const BottomTabs = () => {
  const { setFilter, reset } = useContext(TodoContext);
  const [showTabs, setShowTabs] = useState(false);
  const { tabsContainer } = styles;

  useEffect(() => {
    const keyboardListeners = [
      Keyboard.addListener('keyboardDidShow', () => {
        setShowTabs(false);
      }),
      Keyboard.addListener('keyboardDidHide', () => {
        setShowTabs(true);
      })
    ];

    return () => {
      keyboardListeners &&
        keyboardListeners.forEach(eventListener => eventListener.remove());
    };
  }, []);

  if (!showTabs) {
    return null;
  }


  return (
    <NavigationContainer >
    <Stack.Navigator>

          <Stack.Screen name="Home" component={HomeDrawer}
          options={({ route, navigation }) => ({
            headerTitle: getFocusedRouteNameFromRoute(route),
            headerLeft: () => (
              <Button
                onPress={() =>
                  navigation.dispatch(DrawerActions.toggleDrawer())
                }
                title="Menu"
              />
            ),

          })}/>




    </Stack.Navigator>
    </NavigationContainer>
  );
};

const styles = StyleSheet.create({
  tabsContainer: {
    flexDirection: 'row',
    height: 50,
    borderTopWidth: 1,
    borderTopColor: 'rgba(0, 0, 0 , 0.1)'
  }
});

export default BottomTabs;
